const readline=require("readline"),rl=readline.createInterface({input:process.stdin,output:process.stdout});rl.question("X: ",function(e){rl.question("Y: ",function(n){rl.question("Z: ",function(o){let t=[e,n,o];t.sort(function(e,n){return e-n});var r=+t[0]+ +t[1];console.log(r),rl.close()})})}),rl.on("close",function(){process.exit(0)});